CREATE TABLE "characters" ("id" uuid NOT NULL DEFAULT uuid_generate_v4(),"bio" varchar DEFAULT null,"attributes" varchar DEFAULT null,"description" text DEFAULT null,"image" text DEFAULT null,"expressive_color" varchar DEFAULT null,"created_by" uuid,"created_on" timestamptz DEFAULT now(),"updated_on" timestamptz DEFAULT now(),"status" item_status DEFAULT 'private',"species" uuid,"weapon" uuid,"franchise" uuid,PRIMARY KEY ("id"),CONSTRAINT "fk_gadgets_character" FOREIGN KEY ("weapon") REFERENCES "gadgets"("id"),CONSTRAINT "fk_species_character" FOREIGN KEY ("species") REFERENCES "species"("id"),CONSTRAINT "fk_franchises_character" FOREIGN KEY ("franchise") REFERENCES "franchises"("id"),CONSTRAINT "fk_profiles_character" FOREIGN KEY ("created_by") REFERENCES "profiles"("id"))